mapper.select:
SELECT house_id as houseId, house_type as houseType, deal_type as dealType, house_name as houseName, house_no as houseNo, deal_time as dealTime, city_no as cityNo, locate as locate, area_no as areaNo, realuse as realUse, business_state as businessState, structure as structure, construction_area as constructionArea, pool_area as poolArea, building_date as buildingDate, new_situation as newSituation, street_side as streetSide, street_length as streetLength, depth as depth, total_floor as totalFloor, floor as floor, floor_height as floorHeight, decorate_situation as decorateSituation, land_use_right as landUseRight, land_expired_date as landExpiredDate, total_price as totalPrice, unit_price as unitPrice, hire_state as hireState, hire_price as hirePrice, Supporting_facilities as SupportingFacilities, contact_tel as contactTel, user_id as userId, remark as remark, lng as lng, lat as lat, insert_time as insertTime, img as img, contract as contract, hire_expired_date as hireExpiredDate, hire_unit_price as hireUnitPrice, transfer_fee as transferFee, deposit as deposit, property_management_fee as propertyManagementFee, office_facilities as officeFacilities, parking_facilities as parkingFacilities, pilespacing as pilespacing, crane_beam as craneBeam, span as span FROM tb_house_info where 1=1
and house_type = #{houseType}
and deal_type = #{dealType}
and city_no = #{cityNo}
and locate like '%${locate}%'
and structure = #{structure}
and construction_area ${operator1} #{constructionArea}
and unit_price ${operator2} #{unitPrice}
and building_date = #{buildingDate}
and land_use_right = #{landUseRight}
and deal_time >= #{startDate}
and deal_time <= #{endDate}
order by house_id limit #{start},#{rows}


  SELECT
	count(1)
FROM tb_house_info
where 1=1 



    and list_no = #{listNo}




    and city_no = #{cityNo}




    and locate like '%${locate}%'




    and structure = #{structure}




    and construction_area ${operator1} #{constructionArea}




    and unit_price ${operator2} #{unitPrice}




    and building_date = #{buildingDate}




    and land_use_right = #{landUseRight}




    and deal_time >= #{startDate}




     and deal_time <= #{endDate}



	order by house_id
